<template>
  <div class="page page-public-data-awards">

    <Modal />

    <PageSection
      id="section_0"
      key="section_0"
      :section="headerSection" />

    <div class="main-intro-wrapper-section">

      <PageSection
        id="section_1"
        key="section_1"
        :section="introSection" />

      <BackgroundLayers
        id="intro-wrapper-background-layers"
        layers-array="5_6"
        :breakpoints="wrapperLayersData" />

      <div class="main-content-section">
        <PageSection
          id="section_2"
          key="section_2"
          :section="mainSection" />
        <BackgroundLayers
          id="main-content-background-layers"
          layers-array="2_3_4"
          :breakpoints="mainLayersData" />
      </div>

    </div>

    <PageSection
      id="section_3"
      key="section_3"
      :section="ctaSection" />

  </div>
</template>

<script>
// ====================================================================== Import
import { mapGetters } from 'vuex'
import CloneDeep from 'lodash/cloneDeep'

import PublicDataAwardsPageData from '@/content/pages/public-data-awards.json'

import Modal from '@/components/Modal'
import PageSection from '@/components/PageSection'
import BackgroundLayers from '@/components/BackgroundLayers'

// ====================================================================== Export
export default {
  name: 'PublicDataAwardsPage',

  components: {
    Modal,
    PageSection,
    BackgroundLayers
  },

  data () {
    return {
      tag: 'public_data_awards',
      wrapperLayersData: {
        default: {
          stroke: 1.375,
          radius: 25
        },
        medium: {
          stroke: 1,
          radius: 12.75
        },
        mini: {
          stroke: 0.25,
          radius: 5
        }
      },
      mainLayersData: {
        default: {
          stroke: 1.375,
          radius: 21.5
        },
        medium: {
          stroke: 1,
          radius: 12.75
        },
        mini: {
          stroke: 0.25,
          radius: 5
        }
      }
    }
  },

  async fetch ({ store }) {
    await store.dispatch('global/getBaseData', 'general')
    await store.dispatch('global/getBaseData', { key: 'public_data_awards', data: PublicDataAwardsPageData })
  },

  head () {
    return this.$CompileSeo(this.$GetSeo(this.tag))
  },

  computed: {
    ...mapGetters({
      siteContent: 'global/siteContent'
    }),
    pageContent () {
      const content = CloneDeep(this.siteContent.public_data_awards.page_content)
      return content
    },
    headerSection () {
      return this.pageContent.section_0
    },
    introSection () {
      return this.pageContent.section_1
    },
    mainSection () {
      return this.pageContent.section_2
    },
    ctaSection () {
      return this.pageContent.section_3
    }
  }
}
</script>

