::v-deep #hero-header-text {
  padding-top: 0;
  padding-bottom: 7.5rem;
  .text-block {
    .heading {
      font-size: 4.375rem;
      line-height: leading(65, 70);
      margin-bottom: 1.5rem;
    }
    .subheading {
      font-size: 2.125rem;
      @include fontWeight_SemiBold;
      letter-spacing: 1.1px;
      line-height: leading(40, 34);
    }
  }
}

// /////////////////////////////////////////////////////////////////// Section 1
#section_1 {
  padding-top: 4.125rem; // 1.375rem * 3
  @include mini {
    padding-top: 5rem;
  }
}

::v-deep #logo-gallery {
  position: relative;
  padding-bottom: 0;
  &:before {
    border-top-left-radius: 17.375rem;
  }
  .card-list {
    flex-wrap: nowrap;
    .card {
      margin: 0;
    }
  }
}

::v-deep #intro_1 {
  position: relative;
  &:before {
    border-bottom-left-radius: 5rem;
  }
  img {
    box-shadow: none;
  }
}

::v-deep #logo-gallery,
::v-deep #intro_1 {
  &:before {
    content: '';
    position: absolute;
    top: 0;
    left: calc(#{$backgroundLayers__Left__Desktop} + 4.125rem);
    width: 100%;
    height: 100%;
    background-color: $polar;
  }
}

::v-deep #banner_1 {
